import React, { useState } from "react";
import "./index.css";

const InputField = ({ subject, cname, tabState, input, setInput }) => {
  const [isValid, setIsValid] = useState(true);

  return (
    <div className={tabState < cname ? "field" : "nodisplay"}>
      <label>{subject}</label>
      <br />
      <input
        type="text"
        onChange={(e) => setInput(e.target.value)}
        className={isValid ? "valid" : "invalid"}
      ></input>
    </div>
  );
};

export default InputField;
