import React, { useEffect, useState } from "react";
import "./index.css";
import Login from "./login";
import Modal from "../modalsandwicher/modal";
import MyModal from "../modalsandwicher";

function Home() {
  const [visible, setVisible] = useState(false);
  const [tabState, setTabState] = useState(1);
  const [cart, setCart] = useState(0);
  const [carTotal, setCarTotal] = useState(0);
  const [loggedIn, setLoggedIn] = useState(false);
  const [apiValid, setApiValid] = useState(false);
  const [valiTime, setValiTime] = useState(false);
  const [apidone, setApiDone] = useState(false);

  //login check True/False api
  const putStatus = async (e) => {
    await fetch("http://localhost:3001/logged", {
      method: "PUT",
      headers: {
        Accept: "application.json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(e),
    }).then((response) => {
      if (response.ok) {
        return response.json();
      }
    });
  };
  const getStatus = async () => {
    const x = await fetch("http://localhost:3001/logged", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((result) => {
        return result;
      });
    return x;
  };

  const statuscheck = async () => {
    let x = await getStatus();
    setApiValid(x.status == true);
    setLoggedIn(x.status == true);
    if (x.status == true) {
      setTabState(8);
      setVisible(true);
    }
    return x.status;
  };

  useEffect(() => {
    statuscheck();
  }, []);

  const handleCarTotal = () => {
    if (apidone == true) {
      let q = 0;
      //console.log("asdf" + JSON.stringify(a));
      Object.keys(gridList).map((e, i) => {
        console.log("qwer" + JSON.stringify(gridList[i].cartnum));
        if (gridList[i].cartnum != undefined) {
          q += gridList[i].cartnum;
        }
      });
      setCarTotal(q);
    }
  };

  return (
    <div className={"home"}>
      <div className={"headers"}>
        <span
          className={"logbutton"}
          onClick={() => {
            //loggedIn && setLoggedIn(false);
            setLoggedIn(!loggedIn);
            setVisible(!visible);
            if (loggedIn) {
              setApiValid(false);
              putStatus({ status: "false" });
              setValiTime(false);
            }
            setTabState(1);
          }}
        >
          {apiValid ? "Logout" : "Login"}
        </span>

        <span className={"cart"}>
          ${cart}
          {"  "}
        </span>
        <span className={"cart"}>total:{carTotal}</span>
      </div>
      <Login
        visible={visible}
        setVisible={setVisible}
        tabState={tabState}
        setTabState={setTabState}
        cart={cart}
        setCart={setCart}
        loggedIn={loggedIn}
        setLoggedIn={setLoggedIn}
        apiValid={apiValid}
        setapiValid={setApiValid}
        valiTime={valiTime}
        setValiTime={setValiTime}
        putStatus={putStatus}
        carTotal={carTotal}
        setCarTotal={setCarTotal}
        setApiDone={setApiDone}
      />
      <p />
      <a>guh</a>
      <p />
      {/*<Login
        visible={visible}
        setVisible={setVisible}
        tabState={tabState}
        setTabState={setTabState}
        /> ?????????*/}
      <div className={"footer"}>footer</div>
    </div>
  );
}

export default Home;
