import ModalContent from "./modalcontent";
import Modal from "../../modalsandwicher/modal";
import HomeContent from "./homecontent";
import GridItem from "./gridItem";

const Login = ({
  visible,
  setVisible,
  tabState,
  setTabState,
  cart,
  setCart,
  loggedIn,
  setLoggedIn,
  apiValid,
  setapiValid,
  valiTime,
  setValiTime,
  putStatus,
  carTotal,
  setCarTotal,
