import React, { useState } from "react";
import "./index.css";

const SubmitButton = ({ tabState, setTabState }) => {
  const [buttonName, setButtonName] = useState("Sign In");

  const buttonDirector = ({ tabState }) => {
    if (tabState == 1) {
      setButtonName("Sign In");
    } else if (tabState == 2) {
      setButtonName("Create Account");
    } else if (tabState == 3) {
      setButtonName("Update Password");
    }
  };

  return <button className={"purplebutton"}>{buttonName}</button>;
};

export default SubmitButton;
