import React, { useState } from "react";
import "./index.css";

import MyModal from "../../modalsandwicher";
import ModalContent from "./modalcontent";
import Modal from "../../modalsandwicher/modal";
import HomeContent from "./homecontent";
import GridItem from "./gridItem";

const Login = ({ visible, setVisible, tabState, setTabState }) => {
  //const [visible, setVisible] = useState(false);
  //const [tabState, setTabState] = useState(1);
  const [inputEmail, setInputEmail] = useState(""); //unused
  const [inputPassword, setInputPassword] = useState(""); //unused
  const [inputValue, setInputValue] = useState({ f1: "", f2: "" });
  const [valiTime, setValiTime] = useState(false); //just bring out the input values to ModalContent
  const [preDest, setPreDest] = useState(1);
  const [gridList, setGridList] = useState({
    0: {
      name: "",
      desc: "",
      category: "",
      price: 0,
      quantity: 0,
      img: "",
    },
    1: {
      name: "obj number 1",
      desc: "obj 1",
      category: "obj 1",
      price: 987,
      quantity: 654,
      img: "",
    },
    2: {
      name: "obj number 2",
      desc: "wow",
      category: "sports",
      price: 10,
      quantity: 20,
      img: "",
    },
  });

  const handleVisible = () => {
    setVisible(!visible);
  };

  const handleTab = (e) => {
    setTabState(e);

    /*let j = {};
    for (let i = 0; i++; i < Object.keys(inputValue).length) {
      console.log([Object.keys(inputValue)]);
      j[Object.keys(inputValue)[i]] = "";
    }*/
    setValiTime(false);
    setInputValue({ f1: "", f2: "" });
  };

  const titler = (tabState) => {
    switch (tabState) {
      case 1:
        return "Sign In";
      case 2:
        return "Sign Up";
      case 3:
        return "Update Password";
      case 4:
        return "PASS UPDATE SBEMT";
      case 5:
        return "grid element product";
      case 6:
        return "grid detail product";
      case 7:
        return "add product";
      case 8:
        return "Products";
      default:
        setTabState(1);
        return "";
    }
  };

  return (
    <>
      {/*<div className={"headers"}>
        <span className={"logbutton"} onClick={() => setVisible(!visible)}>
          Login
        </span>
  </div>*/}
      <Modal
        visible={visible}
        tabState={tabState}
        setTabState={setTabState}
        handleVisible={handleVisible}
        titleText={titler(tabState)}
        inputValue={inputValue}
        setInputValue={setInputValue}
        valiTime={valiTime} //name is unclear
        setValiTime={setValiTime}
        handleTab={handleTab}
        gridList={gridList}
        setPreDest={setPreDest}
      >
        <>
          <button onClick={() => setTabState(tabState + 1)}>
            tabstate changer
          </button>
          {tabState != 8 ? (
            <ModalContent
              tabState={tabState}
              setTabState={setTabState}
              inputEmail={inputEmail}
              inputValue={inputValue}
              setInputValue={setInputValue}
              valiTime={valiTime}
              setValiTime={setValiTime}
              handleTab={handleTab}
              gridList={gridList}
              setGridList={setGridList}
              preDest={preDest}
              setPreDest={setPreDest}
            />
          ) : (
            <div>
              {Object.keys(gridList)
                .slice(1, Object.keys(gridList).length + 1)
                .map((e, i) => {
                  return (
                    <GridItem
                      tabState={tabState}
                      setTabState={setTabState}
                      itemnum={i}
                      setPreDest={setPreDest}
                      preDest={preDest}
                      setValiTime={setValiTime}
                      gridList={gridList}
                    />
                  );
                })}

              {/*<GridItem
                tabState={tabState}
                setTabState={setTabState}
                itemnum={1}
                setPreDest={setPreDest}
                preDest={preDest}
                setValiTime={setValiTime}
                gridList={gridList}
              />
              <GridItem
                tabState={tabState}
                setTabState={setTabState}
                itemnum={2}
                setPreDest={setPreDest}
                preDest={preDest}
                setValiTime={setValiTime}
                gridList={gridList}
            />*/}
            </div>
          )}
          <p>tabstate: {tabState}</p>
          <p>{JSON.stringify(gridList)}</p>
          <p>{preDest}</p>
        </>
      </Modal>
    </>
  );
};

export default Login;
