import React, { useState } from "react";
import "./index.css";

const SubmitButton = ({
  tabState,
  setTabState,
  setValiTime,
  listkey,
  setGridList,
  phList,
  gridList,
  itemnum,
  setPreDest,
  preDest,
  putposter,
}) => {
  const buttonDirector = (e) => {
    switch (e) {
      case 1:
        return "Sign In";
      case 2:
        return "Create Account";
      case 3:
        return "Update Password";
      case 5:
        return "Add";
      case 6:
        return "Add to Cart";
      case 7:
        return "Save Product";
      case 8:
        return "Grid";
    }
  };

  const btD2 = (e) => {
    if (e < 4) {
      return "purplebutton";
    } else if (e == 5) {
      return "griditem";
    } else if (e == 6) {
      return "purplebuttonleft";
    } else if (e == 7 || e == 8) {
      return "purplebutton";
    } else {
      return "nobutton";
    }
  };

  const movebehavior = (e) => {
    switch (e) {
      case 5:
        return 7;
      case 6:
        return 8;
      case 7:
        return 8;
      case 8:
        return 7;
      default:
        return 1;
    }
  };

  return (
    <>
      <button
        onClick={() => {
          setValiTime(true);
          if (tabState == 5) {
            setPreDest(itemnum);
          }
          if (tabState == 7) {
            console.log("preDest as of here is");
            console.log({ preDest });
            let y = gridList.slice(1).map((e) => {
              let i = 0;
              if (e.length > 1) {
                i++;
              }
              return i;
            });
            let z = gridList.slice(1, y);
            if (!preDest) {
              let x = Object.keys(gridList).length;
              console.log("the index here of y is " + y);
              setGridList({
                z,
                [y]: phList,
              });
              putposter(y, gridList[y]);
            } else {
              setGridList({ z, [itemnum]: phList });
              putposter(itemnum, gridList[itemnum]);
            }
          }
          if (tabState == 8) {
            setPreDest(gridList.length + 1);
          }
          setTabState(movebehavior(tabState));
        }}
        className={btD2(tabState)}
      >
        {buttonDirector(tabState)}
      </button>
      {"   "}
      <button
        className={tabState == 5 || tabState == 6 ? "griditem2" : "nobutton"}
        onClick={() => {
          setTabState(7);
          if (tabState == 5) {
            setPreDest(itemnum);
          }
          {
            /*if (tabState == 7) {
            setGridList({ ...gridList, [itemnum]: phList });
          }*/
          }
        }}
      >
        Edit
      </button>
    </>
  );
};

export default SubmitButton;
