import React, { useState } from "react";
import SubmitButton from "../modalcontent/submitbutton";
import "./index.css";

const GridItem = ({
  tabState,
  setTabState,
  itemnum,
  setPreDest,
  setValiTime,
  preDest,
  gridList,
}) => {
  return (
    <div className={"gridBorder"}>
      <div className="gridItem">
        <a>
          <img
            onClick={() => {
              setTabState(6);
              setPreDest(itemnum);
            }}
            className="gridImage"
            src={gridList[itemnum].img}
          />{" "}
        </a>
        <p>{gridList[itemnum].name}</p>
        <p>${gridList[itemnum].price}</p>
        <SubmitButton
          tabState={5}
          setTabState={setTabState}
          itemnum={itemnum}
          setPreDest={setPreDest}
          setValiTime={setValiTime}
          preDest={preDest}
        />
      </div>
    </div>
  );
};

export default GridItem;
