    0: "",
    1: "",
    2: "",
    3: "",
  });

  const getProducts = async () => {
    const x = await fetch("http://localhost:3001/gridList", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((result) => {
        /*userName["cartpredest"].map((e) => {
          let a = Object.keys(result).filter((i) => e == i);
          if (a[0] !== undefined) {
            console.log("a[0] - " + a[0]);
            console.log("a[0] usernamecpd - " + userName["cartpredest"]);
            let x = { ...result[e], cartnum: userName["cartpredest"][e] };
            putposter(e, x);
          }
        });*/
        setGridList(result);
        return result;
      });
    return x;
  };

  const putposter = async (i, e) => {
    let x = 0;
    let y = await fetch("http://localhost:3001/gridList/" + i, {
      method: "PUT",
      headers: {
        Accept: "application.json",
        "Content-Type": "application/json",
