import React, { useState } from "react";
import "./index.css";

import MyModal from "../../modalsandwicher";
import ModalContent1 from "./modalcontent1";

const Login = () => {
  const [visible, setVisible] = useState(false);
  const [tabState, setTabState] = useState(1);

  const handleVisible = () => {
    setVisible(!visible);
  };

  const titler = (tabState) => {
    if (tabState == 1) {
      return "sign in";
    } else if (tabState == 2) {
      return "sign up";
    } else if (tabState == 3) {
      return "update password";
    }
  };

  return (
    <>
      <div className={"header"}>
        <span onClick={() => setVisible(!visible)}>Login</span>
      </div>
      <MyModal
        visible={visible}
        tabState={tabState}
        setTabState={setTabState}
        handleVisible={handleVisible}
        titleText={titler(tabState)}
      >
        <>
          <div onClick={() => setTabState(tabState + 1)}>tabstate changer</div>
          <>
            {tabState == 1 ? (
              <ModalContent1 />
            ) : tabState == 2 ? (
              <a>guh2</a>
            ) : (
              <a>fail</a>
            )}
          </>
        </>
      </MyModal>
    </>
  );
};

export default Login;
