function Home() {
  //npx json-server --port 3001 --watch db.json
  const [visible, setVisible] = useState(true);
  const [tabState, setTabState] = useState(8);
  const [cart, setCart] = useState(0);
  const [carTotal, setCarTotal] = useState(0);
  const [loggedIn, setLoggedIn] = useState(true);
  const [apiValid, setApiValid] = useState(true);
  const [valiTime, setValiTime] = useState(false);
  const [apidone, setApiDone] = useState(false);
  const [postApi, setPostApi] = useState(false);
  const [inputValue, setInputValue] = useState({ email: "", pw: "" });
  const [userName, setUserName] = useState({ he: "wgui" });
  const [gridList, setGridList] = useState({
    /*
    0: {
      name: "",
      desc: "",
      category: "",
      price: 0,
      quantity: 0,
      img: "",
    },
    1: {
      name: "obj number 1",
      desc: "obj 1",
      category: "obj 1",
      price: 987,
      quantity: 654,
      img: "https://cdn.onepiecechapters.com/file/CDN-M-A-N/onepiecetcb_1056_014.png",
    },
    2: {
      name: "obj number 2",
      desc: "wow",
      category: "jorts",
      price: 10,
      quantity: 20,
      img: "https://cdn.onepiecechapters.com/file/CDN-M-A-N/onepiecetcb_1056_003.png",
    },*/ 0: "test",
    1: "default gridlist values",
    2: "t",
    3: "",
  });

  const getProducts = async () => {
    const x = await fetch("http://localhost:3001/gridList", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((result) => {
        /*userName["cartpredest"].map((e) => {
          let a = Object.keys(result).filter((i) => e == i);
          if (a[0] !== undefined) {
            console.log("a[0] - " + a[0]);
            console.log("a[0] usernamecpd - " + userName["cartpredest"]);
            let x = { ...result[e], cartnum: userName["cartpredest"][e] };
            putposter(e, x);
          }
        });*/
        setGridList(result);
        return result;
      });
    return x;
  };

  const putposter = async (i, e) => {
    let x = 0;
    await fetch("http://localhost:3001/gridList/" + i, {
      method: "PUT",
      headers: {
        Accept: "application.json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(e),
    }).then((response) => {
      if (response.ok) {
        console.log("ecartnum" + e.cartnum);
        //getProducts();
        setPostApi(true);
        //setCarTotal((carTotal += x));
        return response.json();
      }
    });
  };

  //login check True/False api
  const putStatus = async (e) => {
    await fetch("http://localhost:3001/logged", {
      method: "PUT",
      headers: {
        Accept: "application.json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(e),
    }).then((response) => {
      if (response.ok) {
        return response.json();
      }
    });
  };
  const getStatus = async () => {
    const x = await fetch("http://localhost:3001/logged", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((result) => {
        return result;
      });
    return x;
  };

  const getUser = async () => {
    const x = await fetch("http://localhost:3001/activeuser", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((result) => {
        setUserName(result);

        //result["cartpredest"].map((e)=>{
        getProducts().then((productsresult) => {
          if (result["cartpredest"] != undefined) {
            /*Object.keys(productsresult).map((e, i) => {
              let a = Object.assign(productsresult[i]);
              a.cartnum = 0;
              putposter(i, a);
            });*/
            result["cartpredest"].map((e, j) => {
              let a = Object.keys(productsresult).filter((i) => e == i);
              /*let b = Object.keys(productsresult).filter((i) => e !== i);
              b.map((c) => {
                let y = { ...productsresult[c], cartnum: 0 };
                putposter(c, y);
              });*/
              console.log("a[0] 1- " + a[0]);
              if (a[0] !== undefined) {
                console.log("a[0] - " + a[0]);
                console.log("a[0] usernamecpd - " + result["cartcnum"][j]);
                let x = {
                  ...productsresult[e],
                  cartnum: result["cartcnum"][j],
                };
                putposter(e, x);
              }
            });
          } else {
            console.log("cartassignhehe");
          }
        });
        getProducts();
        return result;
      });
    return x;
  };

  const putuser = async (i, e) => {
    let x = i;
    /*let y = {
      email: e.email,
      pw: e.pw,
      cartpredest: a,
      cartcnum: b,
    };*/
    await fetch("http://localhost:3001/activeuser/", {
      method: "PUT",
      headers: {
        Accept: "application.json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(e),
    });
    await fetch("http://localhost:3001/cartusers/" + i, {
      method: "PUT",
      headers: {
        Accept: "application.json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(e),
    }).then((response) => {
      if (response.ok) {
        setPostApi(true);
        return response.json();
      }
    });
  };

  const putactiveuser = async (e) => {
    await fetch("http://localhost:3001/activeuser/", {
      method: "PUT",
      headers: {
        Accept: "application.json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(e),
    }).then((response) => {
      if (response.ok) {
        return response.json();
      }
    });
  };

  const statuscheck = async () => {
    let x = await getStatus();
    setApiValid(x.status == true);
    setLoggedIn(x.status == true);
    if (x.status == true) {
      setTabState(8);
      setVisible(true);
    }
    return x.status;
  };

  const checkLogin = async (a, b) => {
    let x = await getLogin();
    let y = false;
    let z = "";
    console.log("excalixer: " + JSON.stringify(x[0]["email"]));
    await Object.keys(x).map((e, i) => {
      console.log("excali" + JSON.stringify(x[1]));
      if (x[i]["email"] == a && x[i]["pw"] == b) {
        y = true;
        console.log("usernamer" + x[i]);
        //setUserName(x[i]);
        z = x[i]["email"];
        putactiveuser({
          email: a,
          pw: b,
          cartpredest: x[i].cartpredest,
          cartcnum: x[i].cartcnum,
        }).then((e) => {
          getUser();
        });
      }
    });
    if (y == false) {
      putactiveuser({
        email: "Guest",
        pw: "",
        cartpredest: [],
        cartcnum: [],
      }).then((e) => {
        getUser();
      });
    }
    setApiValid(y);
    putStatus({ status: y });
    return z;
  };

  const getLogin = async () => {
    const x = await fetch("http://localhost:3001/cartusers", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((result) => {
        //setGridList(result);
        return result;
      });
    return x;
  };

  useEffect(() => {
    getUser().then((e) => {
      console.log("iop" + e["email"] + e["pw"]);
      setInputValue({ email: e["email"], pw: e["pw"] });
    });
    statuscheck();
    //setInputValue({ email: userName["email"], pw: userName["pw"] });

    //handleCarTotal();
  }, []);

  useEffect(() => {
    if (!apiValid) {
      setCart(0);
    } else {
      handleCart();
    }
    //handleCarTotal();
  }, [gridList]);

