    let q = 0;
    let b = 0;
    //console.log("asdf" + JSON.stringify(a));
    Object.keys(gridList).map((e, i) => {
      //console.log("qwer" + JSON.stringify(gridList[i].cartnum));
      if (gridList[i].cartnum != undefined && gridList[i].price != undefined) {
        //console.log("cartnum y cartnum" + gridList[i].cartnum);
        //console.log("cartnum y price " + gridList[i].price);
        q = gridList[i].cartnum * +gridList[i].price;
