import React, { useState } from "react";
import "./index.css";

import MyModal from "../../modalsandwicher";
import ModalContent1 from "./modalcontent1";

const Login = () => {
  const [visible, setVisible] = useState(false);
  const [tabState, setTabState] = useState(1);

  const handleVisible = () => {
    setVisible(!visible);
  };

  const titler = (tabState, setTabState = () => {}) => {
    if (tabState == 1) {
      return "Sign In";
    } else if (tabState == 2) {
      return "Sign Up";
    } else if (tabState == 3) {
      return "Update Password";
    } else if (tabState == 4) {
      return " ";
    } else {
      setTabState(1);
    }
  };

  return (
    <>
      <div className={"header"}>
        <span onClick={() => setVisible(!visible)}>Login</span>
      </div>
      <MyModal
        visible={visible}
        tabState={tabState}
        setTabState={setTabState}
        handleVisible={handleVisible}
        titleText={titler(tabState)}
      >
        <>
          <button onClick={() => setTabState(tabState + 1)}>
            tabstate changer
          </button>
          <ModalContent1 />
        </>
      </MyModal>
    </>
  );
};

export default Login;
