old spot*/
  }

  const textest = (e) => {
    return e;
  };

  const idgi = () => {
    console.log(valiTime);
    if (valiTime) {
      return validator(subject, input).validity;
    } else {
      return true;
    }
  };

  return (
    <div
      className={
        tabState >= cname[0] && tabState <= cname[1] ? "field" : "nodisplay"
      }
    >
      {/*textest(subject)*/}
