      <br />
      <input
        type="text"
        value={tabState <= 4 ? inpval : predest}
        onChange={
          tabState <= 4
            ? (e) => {
                setInput(e.target.value);
                setInputValue(e.target.value);
                setValiTime(false);
              }
            : /*setGridList(predest)*/ (e) => setValiTime(false)
        }
        className={idgi() ? "valid" : "invalid"}
      ></input>
      <p className="errormsg">
        {idgi() ? "" : validator(subject, input).error}
      </p>
    </div>
  );
};

export default InputField;
