  setVisible: (isVisible: boolean) => void;
  newwidth: number;
}

const Modal = (props: IProps) => {
  const { children, titleText, visible, setVisible = () => {}, width } = props;
  return (
    <div className={"modalcontent"}>
      <h2>{titleText}</h2>
      <span className={"closer"} onClick={() => setVisible}>
        X
      </span>
      <hr />
      {children}
    </div>
  );
};

export default Modal;
