import React, { useState } from "react";
import "./index.css";

import MyModal from "../../modalsandwicher";
import ModalContent from "./modalcontent";

const Login = () => {
  const [visible, setVisible] = useState(false);
  const [tabState, setTabState] = useState(1);
  //const [inputEmail, setInputEmail] = useState("");
  //const [inputPassword, setInputPassword] = useState("");
  const [inputValue, setInputValue] = useState({ f1: "", f2: "" });
  const [valiTime, setValiTime] = useState(false);

  const handleVisible = () => {
    setVisible(!visible);
  };

  const handleTab = (e) => {
    setTabState(e);

    /*let j = {};
    for (let i = 0; i++; i < Object.keys(inputValue).length) {
      console.log([Object.keys(inputValue)]);
      j[Object.keys(inputValue)[i]] = "";
    }*/
    setValiTime(false);
    setInputValue({ f1: "", f2: "" });
  };

  const titler = (tabState) => {
    if (tabState == 1) {
      return "Sign In";
    } else if (tabState == 2) {
      return "Sign Up";
    } else if (tabState == 3) {
      return "Update Password";
    } else if (tabState == 4) {
      return " ";
    } else {
      setTabState(1);
      return "";
    }
  };

  return (
    <>
      <div className={"header"}>
        <span onClick={() => setVisible(!visible)}>Login</span>
      </div>
      <MyModal
        visible={visible}
        tabState={tabState}
        setTabState={setTabState}
        handleVisible={handleVisible}
        titleText={titler(tabState)}
        inputValue={inputValue}
        setInputValue={setInputValue}
        valiTime={valiTime}
        setValiTime={setValiTime}
        handleTab={handleTab}
      >
        <>
          {/*<button onClick={() => setTabState(tabState + 1)}>
            tabstate changer
  </button>*/}
          <ModalContent
            tabState={tabState}
            setTabState={setTabState}
            inputEmail={inputEmail}
            setInputEmail={setInputEmail}
            inputPassword={inputPassword}
            setInputPassword={setInputPassword}
            inputValue={inputValue}
            setInputValue={setInputValue}
            valiTime={valiTime}
            setValiTime={setValiTime}
            handleTab={handleTab}
          />
        </>
      </MyModal>
    </>
  );
};

export default Login;
