  };

  const titler = (tabState) => {
    switch (tabState) {
      case 1:
        return "Sign In";
      case 2:
        return "Sign Up";
      case 3:
        return "Update Password";
      case 4:
        return "PASS UPDATE SBEMT";
      case 5:
        return "grid element product";
      case 6:
        //return "grid detail product";
        return "Product Details";
      case 7:
        return "add product";
      case 8:
        return "Products";
      default:
        setTabState(1);
        return "";
