import React, { useEffect, useState } from "react";
import "./index.css";
import Login from "./login";
import Modal from "../modalsandwicher/modal";
import MyModal from "../modalsandwicher";

function Home() {
  //npx json-server --port 3001 --watch db.json
  const [visible, setVisible] = useState(true);
  const [tabState, setTabState] = useState(8);
  const [cart, setCart] = useState(0);
  const [carTotal, setCarTotal] = useState(0);
  const [loggedIn, setLoggedIn] = useState(true);
  const [apiValid, setApiValid] = useState(true);
  const [valiTime, setValiTime] = useState(false);
  const [apidone, setApiDone] = useState(false);
  const [postApi, setPostApi] = useState(false);
  const [inputValue, setInputValue] = useState({ email: "", pw: "" });
  const [userName, setUserName] = useState({ he: "wgui" });
  const [gridList, setGridList] = useState({
    /*
    0: {
      name: "",
      desc: "",
      category: "",
      price: 0,
      quantity: 0,
      img: "",
    },
    1: {
      name: "obj number 1",
      desc: "obj 1",
      category: "obj 1",
      price: 987,
      quantity: 654,
      img: "https://cdn.onepiecechapters.com/file/CDN-M-A-N/onepiecetcb_1056_014.png",
    },
    2: {
      name: "obj number 2",
      desc: "wow",
      category: "jorts",
      price: 10,
      quantity: 20,
      img: "https://cdn.onepiecechapters.com/file/CDN-M-A-N/onepiecetcb_1056_003.png",
    },*/ 0: "test",
    1: "default gridlist values",
    2: "t",
    3: "",
  });

  //login check True/False api
  const putStatus = async (e) => {
    await fetch("http://localhost:3001/logged", {
      method: "PUT",
      headers: {
        Accept: "application.json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(e),
    }).then((response) => {
      if (response.ok) {
        return response.json();
      }
    });
  };
  const getStatus = async () => {
    const x = await fetch("http://localhost:3001/logged", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((result) => {
        return result;
      });
    return x;
  };

  const getUser = async () => {
    const x = await fetch("http://localhost:3001/activeuser", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((result) => {
        setUserName(result);
        return result;
      });
    return x;
  };

  const putuser = async (i, e, a, b) => {
    let x = i;
    let y = { ...e, cartpredest: a, cartcnum: b };
    await fetch("http://localhost:3001/activeuser/", {
      method: "PUT",
      headers: {
        Accept: "application.json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(y),
    });
    await fetch("http://localhost:3001/users/" + i, {
      method: "PUT",
      headers: {
        Accept: "application.json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(y),
    }).then((response) => {
      if (response.ok) {
        setPostApi(true);
        return response.json();
      }
    });
  };

  const statuscheck = async () => {
    let x = await getStatus();
    setApiValid(x.status == true);
    setLoggedIn(x.status == true);
    if (x.status == true) {
      setTabState(8);
      setVisible(true);
    }
    return x.status;
  };

  const checkLogin = async (a, b) => {
    let x = await getLogin();
    let y = false;
    let z = "";
    console.log("excalixer: " + JSON.stringify(x[0]["email"]));
    await Object.keys(x).map((e, i) => {
      console.log("excali" + JSON.stringify(x[1]));
      if (x[i]["email"] == a && x[i]["pw"] == b) {
        y = true;
        console.log("usernamer" + x[i]);
        //setUserName(x[i]);
        z = x[i]["email"];
      }
    });
    setApiValid(y);
    putStatus({ status: y });
    return z;
  };

  const getLogin = async () => {
    const x = await fetch("http://localhost:3001/users", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((result) => {
        //setGridList(result);
        return result;
      });
    return x;
  };

  useEffect(() => {
    getUser().then((e) => {
      console.log("iop" + e["email"] + e["pw"]);
    });
    statuscheck();
    //setInputValue({ email: userName["email"], pw: userName["pw"] });

    //handleCarTotal();
  }, []);

  useEffect(() => {
    handleCart();
    //handleCarTotal();
  }, [gridList]);

  const getter = async () => {};

  const handleCart = async () => {
    let q = 0;
    let b = 0;
    //console.log("asdf" + JSON.stringify(a));
    Object.keys(gridList).map((e, i) => {
      console.log("qwer" + JSON.stringify(gridList[i].cartnum));
      if (gridList[i].cartnum != undefined) {
        q = gridList[i].cartnum * gridList[i].price;
        b += q;
        //console.log("fdsa" + gridList[i].cartnum);
      }
    });
    //console.log("fdsa" + b);
    setCart(b);
    return b;
  };

  return (
    <div className={"home"}>
      <div className={"headers"}>
        <span
          className={"logbutton"}
          onClick={() => {
            //loggedIn && setLoggedIn(false);
            setLoggedIn(!loggedIn);
            //setVisible(!visible);
            if (loggedIn) {
              setApiValid(false);
              putStatus({ status: "false" });
              setValiTime(false);
            }
            setTabState(1);
          }}
        >
          {apiValid ? "Logout" : "Login"}
        </span>
        <a>
          {userName["email"]}
          {"     "}
        </a>

        <span className={"cart"}>
          ${cart}
          {"  "}
        </span>
        <span className={"cart"}>
          total:
          {carTotal}
        </span>
      </div>
      <Login
        visible={visible}
        setVisible={setVisible}
        tabState={tabState}
        setTabState={setTabState}
        cart={cart}
        setCart={setCart}
        loggedIn={loggedIn}
        setLoggedIn={setLoggedIn}
        apiValid={apiValid}
        setapiValid={setApiValid}
        valiTime={valiTime}
        setValiTime={setValiTime}
        putStatus={putStatus}
        carTotal={carTotal}
        setCarTotal={setCarTotal}
        setApiDone={setApiDone}
        gridList={gridList}
        setGridList={setGridList}
        getLogin={getLogin}
        checkLogin={checkLogin}
        userName={userName}
        setUserName={setUserName}
        putuser={putuser}
        postApi={postApi}
        setPostApi={setPostApi}
        inputValue={inputValue}
        setInputValue={setInputValue}
      />
      <p />
      <a>guh</a>
      <p />
      {/*<Login
        visible={visible}
        setVisible={setVisible}
        tabState={tabState}
        setTabState={setTabState}
        /> ?????????*/}
      <div className={"footer"}>footer</div>
    </div>
  );
}

export default Home;
