import React, { useState, useEffect } from "react";
import "./index.css";

import MyModal from "../../modalsandwicher";
import ModalContent from "./modalcontent";
import Modal from "../../modalsandwicher/modal";
import HomeContent from "./homecontent";
import GridItem from "./gridItem";

const Login = ({ visible, setVisible, tabState, setTabState }) => {
  //const [visible, setVisible] = useState(false);
  //const [tabState, setTabState] = useState(1);
  const [inputEmail, setInputEmail] = useState(""); //unused
  const [inputPassword, setInputPassword] = useState(""); //unused
  const [inputValue, setInputValue] = useState({ f1: "", f2: "" });
  const [valiTime, setValiTime] = useState(false); //just bring out the input values to ModalContent
  const [preDest, setPreDest] = useState(0);
  const [jsonstate, setJsonState] = useState();
  const [apidone, setApiDone] = useState(false);
  const [gridList, setGridList] = useState({
    0: {
      name: "2",
      desc: "",
      category: "",
      price: 0,
      quantity: 0,
      img: "",
    },
    1: {
      name: "obj number 1",
      desc: "obj 1",
      category: "obj 1",
      price: 987,
      quantity: 654,
      img: "https://cdn.onepiecechapters.com/file/CDN-M-A-N/onepiecetcb_1056_014.png",
    },
    2: {
      name: "obj number 2",
      desc: "wow",
      category: "sports",
      price: 10,
      quantity: 20,
      img: "https://cdn.onepiecechapters.com/file/CDN-M-A-N/onepiecetcb_1056_003.png",
    },
  });

  const handleVisible = () => {
    setVisible(!visible);
  };

  const handleTab = (e) => {
    setTabState(e);

    /*let j = {};
    for (let i = 0; i++; i < Object.keys(inputValue).length) {
      console.log([Object.keys(inputValue)]);
      j[Object.keys(inputValue)[i]] = "";
    }*/
    setValiTime(false);
    setInputValue({ f1: "", f2: "" });
  };

  const titler = (tabState) => {
    switch (tabState) {
      case 1:
        return "Sign In";
      case 2:
        return "Sign Up";
      case 3:
        return "Update Password";
      case 4:
        return "PASS UPDATE SBEMT";
      case 5:
        return "grid element product";
      case 6:
        //return "grid detail product";
        return "Product Details";
      case 7:
        return "add product";
      case 8:
        return "Products";
      default:
        setTabState(1);
        return "";
    }
  };

  /* const getCustomerApi = async () => {
    const response = await fetch(`../../../../db.json`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    });
    return response;
  };*/
  const getProducts = async () => {
    let x = await fetch("http://localhost:3001/database", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((result) => {
        console.log("result ==" + JSON.stringify(result[0][1].name));
        return result;
      });
    return x;
    /*.then((res) => res.json())
      .then((result) => {
        console.log("incoming result");
        //console.log(result[0][1]);
        console.log(result);
      })
      .catch(console.log);*/

    //return x.json();
    /*const y = await x.json();
    console.log("use2 = " + JSON.stringify(y));
    return y;*/
  };
  const changeposter = async () => {
    let changeProducts = await fetch("http://localhost:3001/database/", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(gridList),
    });
    /*.then((res) => res.json())
      .then((result) => {
        console.log("incoming changed result");
        //console.log(result[0][1]);
        console.log(result);
      })
      .catch(console.log);*/
    return changeProducts;
  };

  const jsonformatter = async () => {
    if (!apidone) {
      let x = await getProducts();
      console.log("nannyexpress style - " + JSON.stringify(x[0][0]));
      console.log("jsonstate");
      setApiDone(true);
      setJsonState(await getProducts());
      return x;
    } else {
      let x = {};
      return x;
    }
  };

  useEffect(() => {
    jsonformatter();
    console.log(apidone ? jsonstate![0][0]["name"] : "failed again");
    //changeposter();
    /*async function apitry() {
      const x = await getCustomerApi();
      console.log("api attempt");
      console.log(JSON.stringify(x));
    }*/
    /*() => {
      fetch("http://localhost:3001/posts", {
        method: "GET",
        headers: { "Content-Type": "application/json" },
      }).then((response) => {
        if (!response.ok) {
          console.log("wtf is happenin");
          throw new Error("idk wtf is goin on");
        }
        console.log("this is the fetch result");
        console.log(response.json());
        return response.json();
      });
    };*/
    //changeProducts();
  }, []);

  return (
    <>
      {console.log("fetch result")}
      {console.log("before getProducts")}
      {/*{async () => {
        console.log("usuable gedsfat" + (await getProducts()));
      }}*/}{" "}
      {/* for some reason this never showed up on console, also disappeared "use2" */}
      {console.log("after getProducts")}
      {console.log("gridList stringify - " + JSON.stringify({ gridList }))}
      {/*<a>jsonguh{apidone ? jsonstate![0][1]["name"] : "nothin"}</a>*/}
      {/*{changeposter().then((e) => console.log("change attempt " + e))}*/}
      {/*<div className={"headers"}>
        <span className={"logbutton"} onClick={() => setVisible(!visible)}>
          Login
        </span>
  </div>*/}
      <Modal
        visible={visible}
        tabState={tabState}
        setTabState={setTabState}
        handleVisible={handleVisible}
        titleText={titler(tabState)}
        inputValue={inputValue}
        setInputValue={setInputValue}
        valiTime={valiTime} //name is unclear
        setValiTime={setValiTime}
        handleTab={handleTab}
        gridList={gridList}
        setPreDest={setPreDest}
      >
        <>
          <button onClick={() => setTabState(tabState + 1)}>
            tabstate changer
          </button>
          {tabState != 8 ? (
            <ModalContent
              tabState={tabState}
              setTabState={setTabState}
              inputEmail={inputEmail}
              inputValue={inputValue}
              setInputValue={setInputValue}
              valiTime={valiTime}
              setValiTime={setValiTime}
              handleTab={handleTab}
              gridList={gridList}
              setGridList={setGridList}
              preDest={preDest}
              setPreDest={setPreDest}
            />
          ) : (
            <div>
              <p>{Object.keys(gridList).slice(1)}</p>
              {Object.keys(gridList)
                .slice(1)
                .map((e, i) => {
                  return (
                    <>
                      {console.log("id" + i)}
                      <GridItem
                        key={"id" + i}
                        tabState={tabState}
                        setTabState={setTabState}
                        itemnum={i + 1}
                        setPreDest={setPreDest}
                        preDest={preDest}
                        setValiTime={setValiTime}
                        gridList={gridList}
                      />
                    </>
                  );
                })}

              {/*<GridItem
                tabState={tabState}
                setTabState={setTabState}
                itemnum={1}
                setPreDest={setPreDest}
                preDest={preDest}
                setValiTime={setValiTime}
                gridList={gridList}
              />
              <GridItem
                tabState={tabState}
                setTabState={setTabState}
                itemnum={2}
                setPreDest={setPreDest}
                preDest={preDest}
                setValiTime={setValiTime}
                gridList={gridList}
            />*/}
            </div>
          )}
          <p>tabstate: {tabState}</p>
          <p>{JSON.stringify(gridList)}</p>
          <p>preDest: {preDest}</p>
        </>
      </Modal>
    </>
  );
};

export default Login;
