import React, { useState } from "react";
import "./index.css";

interface IProps {
  titleText?: string;
  visible?: boolean;
  children?: JSX.Element;
  tabState: number;
  handleVisible: () => void;
  setTabState: (arg0: number) => void;
  handleTab: (arg0: number) => void;
  width: number;
}

const Modal = (props: IProps) => {
  const {
    children,
    titleText,
    setTabState = () => {},
    visible,
    handleVisible = () => {},
    tabState,
    width,
    handleTab,
  } = props;
  return (
    <>
      <div
        className={
          visible ? (tabState < 5 ? "modalopen" : "modalopenII") : "modalclosed"
        }
      >
        {tabState < 5 ? (
          <div className={"modalcontent"}>
            <h2>{titleText}</h2>
            <span
              className={"closer"}
              onClick={() => {
                handleVisible();
                handleTab(1);
              }}
            >
              X
            </span>
            {/*<hr />*/}
            {children}
          </div>
        ) : tabState > 7 ? (
          <div className={"modalcontent"}>grid container</div>
        ) : tabState == 5 ? (
          <div className={"modalcontent"}>
            <p>{titleText}</p> grid element
            {children}
            <button>add</button> <button>edit</button>
          </div>
        ) : (
          <div className={"modalcontent"}>
            <h2>{titleText}</h2> any of the 3 phase 2 modals {children}
          </div>
        )}
      </div>
    </>
  );
};

export default Modal;
