import React, { useState, useEffect } from "react";
import "./index.css";
import Login from "../..";

const SubmitButton = ({
  tabState,
  setTabState,
  setValiTime,
  listkey,
  setGridList,
  phList,
  gridList,
  itemnum,
  setPreDest,
  preDest,
  putuser,
  validator,
  cart,
  setCart,
  putposter,
  apiValid,
  checkLogin,
  inputValue,
  setInputValue,
  getLogin,
  putStatus,
  carTotal,
  setCarTotal,
  userName,
  setUserName,
}) => {
  //const [apiValid, setApiValid] = useState(false);
  const [plusMin, setPlusMin] = useState(false);
  const [wtfupdate, setWtfupdate] = useState(0);
  const buttonDirector = (e) => {
    switch (e) {
      case 1:
        return "Sign In";
      case 2:
        return "Create Account";
      case 3:
        return "Update Password";
      case 5:
        return "Add";
      case 6:
        return "Add to Cart";
      case 7:
        return "Save Product";
      case 8:
        return "Grid";
    }
  };

  const btD2 = (e) => {
    if (e < 4) {
      return "purplebutton";
    } else if (e == 5) {
      return "griditem";
    } else if (e == 6) {
      return "purplebuttonleft";
    } else if (e == 7 || e == 8) {
      return "purplebutton";
    } else {
      return "nobutton";
    }
  };

  const movebehavior = (e) => {
    switch (e) {
      case 1:
        return 8;
      case 2:
        return 1;
      case 3:
        return 4;
      case 5:
        return 8;
      case 6:
        return 8;
      case 7:
        return 8;
      case 8:
        return 7;
      default:
        return 1;
    }
  };

  const numfix = (e) => {
    return e;
  };

  const getProducts = async () => {
    const x = await fetch("http://localhost:3001/gridList", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((result) => {
        //console.log("result ==" + JSON.stringify(result[0][1].name));
        return result;
      });
    return x;
  };

  const setholder = async () => {
    try {
      let x = await getProducts();
      setGridList(await getProducts());
    } catch (error) {
      console.log("submitbutton cant find functions??? - " + error);
    }
  };

  const signupholder = async () => {
    try {
      let x = await getLogin();
      let y = 0;
      for (let i in Object.keys(x)) {
        if (Object.keys(x[i]).length > 1) {
          y++;
        }
      }
      let q = { ...inputValue, cartpredest: [], cartcnum: [] };
      putuser(y + 1, q);
    } catch (error) {
      console.log("signup error - " + error);
    }
  };

  const plusminner = async () => {
    let x = await gridList[itemnum].cartnum;
    if (x > 0) {
      setPlusMin(true);
    }
    /*if (!x) {
      setCarTotal(+carTotal + 0);
    } else {
      setCarTotal((await carTotal) + (await x));
    }*/
    return x;
  };

  const userupdateid = async (e) => {
    try {
      let x = await getLogin();
      let y = 0;
      //console.log("iop y - " + y);
      Object.keys(x).map((g, i) => {
        /*console.log("iop x keys - " + Object.keys(x));
        console.log("iop i - " + i);
        console.log("iop i[email] - " + x[i].email);
        console.log("iop e - " + e);
        console.log("iop x - " + JSON.stringify(x));*/
        if (x[i].email !== undefined && x[i].email == e) {
          y = i;
        }
      });
      console.log("iop y - " + y);
      return y + 1;
    } catch (error) {
      console.log("signup error - " + error);
      return 7;
    }
  };

  const handleid = async (e) => {
    let x = await userupdateid(e);
    return x;
  };

  /*const handleUserNamep = (a) => {
    let x = Object.keys(gridList).map((e, i) => {
      let d = a.filter((y)=> i + 1 == y)
      return d[0];
      });
    console.log("poi c" + c);
    x.forEach((e) => {
      console.log("poi x" + x);
    });
    return [2, 2, 2];
  };*/

  const handleUserNamec = (e) => {
    let a = [];
    let x = Object.keys(gridList).map((e, i) => {
      if (gridList[i].cartnum > 0) {
        a.push(i);
        return gridList[i].cartnum;
      }
    });
    let y = x.filter((e) => {
      return e !== undefined;
    });
    console.log("poi y" + y);
    console.log("poi a" + a);
    if (e == "predest") {
      return a;
    } else if (e == "cartnum") {
      return y;
    } else return [1, 2, 4];
  };

  useEffect(() => {
    plusminner();
    /*if (gridList[itemnum].cartnum == 0) {
      let a = Object.assign(gridList);
      setWtfupdate(wtfupdate + 1);
      a[itemnum].cartnum++;
      putposter(itemnum, a[itemnum]);
    }*/
  }, [gridList[itemnum].cartnum, gridList]);

  return (
    <>
      <button
        onClick={() => {
          if (tabState == 1) {
            checkLogin(inputValue.email, inputValue.pw);
          }
          if (tabState == 2) {
            signupholder();
          }
          if (tabState < 4) {
            try {
              setValiTime(true);
            } catch {}
          } else {
            try {
              setValiTime(false);
            } catch {}
          }
          if (tabState == 5) {
            if (!plusMin) {
              setPreDest(itemnum);
              setCart(+cart + +gridList[itemnum].price);
              let a = Object.assign(gridList);
              setPlusMin(true);
              a[itemnum].cartnum++;
              setGridList(a);
              putposter(itemnum, a[itemnum]); //.then(getProducts());
              //userupdateid("lumber@gmail.com");
              //
              //let z = handleid(userName.email);
              let z = handleid(userName.email).then((e) => {
                console.log("iop" + e);
                return e;
              });
              let q = {
                email: userName.email,
                pw: userName.pw,
                cartpredest: handleUserNamec("predest"),
                cartcnum: handleUserNamec("cartnum"),
              };
              console.log("putuser");
              userupdateid(userName.email).then((e) => {
                putuser(
                  e,
                  q
                  //handleUserNamep(),
                  //handleUserNamec(handleUserNamep())
                );
              });
            }
          }
          if (tabState == 6) {
            if (!plusMin) {
              setPreDest(itemnum);
              setCart(+cart + +gridList[itemnum].price);
              let a = Object.assign(gridList);
              setPlusMin(true);
              a[itemnum].cartnum++;
              setGridList(a);
              putposter(itemnum, a[itemnum]); //.then(getProducts());
            }
          }
          if (tabState == 7) {
            console.log("preDest as of here is");
            console.log({ preDest });
            let y = 1;
            for (let i in Object.keys(gridList).slice(1)) {
              if (Object.keys(gridList[i]).length > 3) {
                y++;
              }
            }
            let z = gridList.slice(1, y);
            if (preDest == 0) {
              let x = Object.keys(gridList).length;
              console.log("the index here of y is " + y);
              /*setGridList({
                ...gridList,
                [y]: phList,
              });*/
              console.log("calculated y aka i is" + y);
              putposter(y - 1, phList); //.then(getProducts());
            } else {
              putposter(itemnum, phList); //.then(getProducts());
            }
            setholder();
            y = 0;
          }
          if (tabState == 8) {
            let y = 0;
            for (let i in Object.keys(gridList).slice(1)) {
              y++;
              if (Object.keys(gridList[i]).length == 3) {
                break;
              }
            }
            setPreDest(y);
          }
          setTabState(
            tabState < 4 && !apiValid ? movebehavior(0) : movebehavior(tabState)
          );
        }}
        className={btD2(tabState)}
      >
        {(tabState == 5 || tabState == 6) && plusMin ? (
          <>
            <span
              className="minus"
              onClick={() => {
                let a = Object.assign(gridList);
                setWtfupdate(wtfupdate - 1);
                if (a[itemnum].cartnum == 1) {
                  setPlusMin(false);
                  setWtfupdate(0);
                }
                a[itemnum].cartnum--;
                setGridList(a);
                putposter(itemnum, a[itemnum]); //.then(getProducts());
                let q = {
                  email: userName.email,
                  pw: userName.pw,
                  cartpredest: handleUserNamec("predest"),
                  cartcnum: handleUserNamec("cartnum"),
                };
                userupdateid(userName.email).then((e) => {
                  putuser(e, q);
                });
              }}
            >
              -
            </span>
            <a>{gridList[itemnum].cartnum}</a>
            <span
              className="plus"
              onClick={() => {
                let a = Object.assign(gridList);
                setWtfupdate(wtfupdate + 1);
                a[itemnum].cartnum++;
                setGridList(a);
                putposter(itemnum, a[itemnum]); //.then(getProducts());
                let q = {
                  email: userName.email,
                  pw: userName.pw,
                  cartpredest: handleUserNamec("predest"),
                  cartcnum: handleUserNamec("cartnum"),
                };
                userupdateid(userName.email).then((e) => {
                  putuser(e, q);
                });
              }}
            >
              +
            </span>
          </>
        ) : (
          <>{buttonDirector(tabState)}</>
        )}
      </button>
      {"   "}
      <button
        className={
          (tabState == 5 || tabState == 6) && apiValid
            ? "griditem2"
            : "nobutton"
        }
        onClick={() => {
          setTabState(7);
          if (tabState == 5) {
            setPreDest(itemnum);
          }
          {
            /*if (tabState == 7) {
            setGridList({ ...gridList, [itemnum]: phList });
          }*/
          }
        }}
      >
        Edit
      </button>
    </>
  );
};

export default SubmitButton;
