import React, { useState, useEffect } from "react";
import "./index.css";
import Login from "../..";

const SubmitButton = ({
  tabState,
  setTabState,
  setValiTime,
  listkey,
  setGridList,
  phList,
  gridList,
  itemnum,
  setPreDest,
  preDest,
  putuser,
  validator,
  cart,
  setCart,
  putposter,
  apiValid,
  checkLogin,
  inputValue,
  setInputValue,
  putStatus,
  carTotal,
  setCarTotal,
  userName,
}) => {
  //const [apiValid, setApiValid] = useState(false);
  const [plusMin, setPlusMin] = useState(false);
  const [wtfupdate, setWtfupdate] = useState(0);
  //const [deleteDone, setDeleteDone] = useState(true);
  const buttonDirector = (e) => {
    switch (e) {
      case 1:
        return "Sign In";
      case 2:
        return "Create Account";
      case 3:
        return "Update Password";
      case 5:
        return "Add";
      case 6:
        return "Add to Cart";
      case 7:
        return "Save Product";
      case 8:
        return "Grid";
    }
  };

  const btD2 = (e) => {
    if (e < 4) {
      return "purplebutton";
    } else if (e == 5) {
      return "griditem";
    } else if (e == 6) {
      return "purplebuttonleft";
    } else if (e == 7 || e == 8) {
      return "purplebutton";
    } else {
      return "nobutton";
    }
  };

  const movebehavior = (e) => {
    switch (e) {
      case 1:
        return 8;
      case 2:
        return 1;
      case 3:
        return 4;
      case 5:
        return 8;
      case 6:
        return 8;
      case 7:
        return 8;
      case 8:
        return 7;
      default:
        return 1;
    }
  };

  const numfix = (e) => {
    return e;
  };

  const getProducts = async () => {
    const x = await fetch("http://localhost:3002/gridList", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((result) => {
        //console.log("result ==" + JSON.stringify(result[0][1].name));
        return result;
      });
    return x;
  };

  const getLogin = async () => {
    const x = await fetch("http://localhost:3002/cartusers", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((result) => {
        //setGridList(result);
        return result;
      });
    return x;
  };

  const putpw = async (i, e) => {
    let x = await fetch("http://localhost:3002/cartusers/" + i, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then(async (response) => {
        let q = { ...response, pw: e };
        await fetch("http://localhost:3002/cartusers/" + i, {
          method: "PUT",
          headers: {
            Accept: "application.json",
            "Content-Type": "application/json",
          },
          body: JSON.stringify(q),
        });
        return response.json();
      });
    return x;
  };

  const setholder = async () => {
    try {
      let x = await getProducts();
      setGridList(await getProducts());
    } catch (error) {
      console.log("submitbutton cant find functions??? - " + error);
    }
  };

  const signupholder = async () => {
    try {
      let x = await getLogin();
      let y = 0;
      for (let i in Object.keys(x)) {
        if (Object.keys(x[i]).length > 1) {
          y++;
        }
      }
      let q = { ...inputValue, cartpredest: [], cartcnum: [] };
      console.log("tab 2 clicked");
      putuser(y + 1, q);
    } catch (error) {
      console.log("signup error - " + error);
    }
  };

  const plusminner = async () => {
    let x = await gridList[itemnum].cartnum;
    if (x > 0) {
      setPlusMin(true);
    } else {
      setPlusMin(false);
    }
    /*if (!x) {
      setCarTotal(+carTotal + 0);
    } else {
      setCarTotal((await carTotal) + (await x));
    }*/
    return x;
  };

  const userupdateid = async (e) => {
    try {
      let x = await getLogin();
      let y = 0;
      //console.log("iop y - " + y);
      Object.keys(x).map((g, i) => {
        /*console.log("iop x keys - " + Object.keys(x));
        console.log("iop i - " + i);
        console.log("iop i[email] - " + x[i].email);
        console.log("iop e - " + e);
        console.log("iop x - " + JSON.stringify(x));*/
        if (x[i].email !== undefined && x[i].email == e) {
          y = i;
          console.log("signup nonerror - " + y);
        }
      });
      //setErrClassName("he");
      //console.log("iop y - " + y);
      return y;
    } catch (error) {
      // setErrClassName("");
      console.log("signup error - " + error);
      //return 7;
    }
  };

  const handleid = async (e) => {
    let x = await userupdateid(e);
    return x;
  };

  /*const handleUserNamep = (a) => {
    let x = Object.keys(gridList).map((e, i) => {
      let d = a.filter((y)=> i + 1 == y)
      return d[0];
      });
    console.log("poi c" + c);
    x.forEach((e) => {
      console.log("poi x" + x);
    });
    return [2, 2, 2];
  };*/

  const handleUserNamec = (e) => {
    let a = [];
    let x = Object.keys(gridList).map((e, i) => {
      if (gridList[i].cartnum > 0) {
        a.push(i);
        return gridList[i].cartnum;
      }
    });
    let y = x.filter((q) => {
      return q !== undefined;
    });
    //console.log("poi y" + y);
    //console.log("poi a" + a);
    if (e == "predest") {
      return a;
    } else if (e == "cartnum") {
      return y;
    } else return [1, 2, 4];
  };

  useEffect(() => {
    plusminner();
    /*if (gridList[itemnum].cartnum == 0) {
      let a = Object.assign(gridList);
      setWtfupdate(wtfupdate + 1);
      a[itemnum].cartnum++;
      putposter(itemnum, a[itemnum]);
    }*/
  }, [gridList[itemnum].cartnum, gridList]);

  /*useEffect(() => {
    // console.log("deleteDone");
    if (
      userName.email !== undefined &&
      userName.email !== "Guest" &&
      handleUserNamec("predest") !== []
    ) {
      let q = {
        email: userName.email,
        pw: userName.pw,
        cartpredest: handleUserNamec("predest"),
        cartcnum: handleUserNamec("cartnum"),
      };
      //console.log("deleteDone" + userName.email);
      console.log("signup deleteDone" + userName.email);
      userupdateid(userName.email).then((e) => {
        putuser(e, q);
        //console.log("deleteDone r");
      });
    }
  }, [deleteDone]);*/ //

  return (
    <>
      <button
        onClick={() => {
          if (tabState == 1) {
            console.log("tab 1sb inputvalue email- " + inputValue.email);
            checkLogin(inputValue.email, inputValue.pw);
          }
          if (tabState == 2) {
            signupholder();
          }
          if (tabState == 3) {
            putpw(userupdateid(inputValue.email), inputValue.pw);
          }
          if (tabState < 5) {
            setValiTime(true);
          } else {
            try {
              setValiTime(false);
            } catch {}
          }
          if (tabState == 5) {
            if (!plusMin) {
              setPreDest(itemnum);
              setCart(+cart + +gridList[itemnum].price);
              let a = Object.assign(gridList);
              setPlusMin(true);
              a[itemnum].cartnum++;
              setGridList(a);
              putposter(itemnum, a[itemnum]); //.then(getProducts());
              //userupdateid("lumber@gmail.com");
              //
              //let z = handleid(userName.email);
              let z = handleid(userName.email).then((e) => {
                // console.log("iop" + e);
                return e;
              });

              let q = {
                email: userName.email,
                pw: userName.pw,
                cartpredest: handleUserNamec("predest"),
                cartcnum: handleUserNamec("cartnum"),
              };
              //console.log("putuser");
