import React, { useState } from "react";
import "./index.css";

const InputField = ({
  subject,
  cname,
  tabState,
  input,
  setInput,
  valiTime,
  validator,
  setValiTime,
  inpval,
}) => {
  {
    /*const [isValid, setIsValid] = useState(true); 
old spot*/
  }

  const textest = (e) => {
    return e;
  };

  const idgi = () => {
    console.log(valiTime);
    if (valiTime) {
      return validator(subject, input).validity;
    } else {
      return true;
    }
  };

  return (
    <div className={tabState < cname ? "field" : "nodisplay"}>
      {/*textest(subject)*/}
      <label>{subject}</label>
      <br />
      <input
        type="text"
        value={inpval}
        onChange={(e) => {
          setInput(e.target.value);
          setValiTime(false);
        }}
        className={idgi() ? "valid" : "invalid"}
      ></input>
      <p className="errormsg">
        {idgi() ? "" : validator(subject, input).error}
      </p>
    </div>
  );
};

export default InputField;
