import React, { useRef, useState } from "react";
import { json } from "stream/consumers";
import "./index.css";

import InputField from "./inputfield";
import SubmitButton from "./submitbutton";

const ModalContent = ({
  setTabState,
  tabState,
  inputEmail,
  setInputEmail,
  inputPassword,
  setInputPassword,
}) => {
  const [preEmail, setPreEmail] = useState("");
  const [prePass, setPrePass] = useState("");
  const [valiTime, setValiTime] = useState(false);
  const [inputValue, setInputValue] = useState({ f1: "", f2: "" });
  const fieldBlank = useRef(null);

  const validator = (inputtype, e) => {
    let validresult = { error: "", validity: true };
    //setInterval(() => setValiTime(false), 10000);
    console.log(inputtype);

    if (inputtype === "Email") {
      if (e.length > 10) {
        console.log("a");
        console.log(e.slice(e.length - 10));
        console.log("wtf");
        if (e.slice(e.length - 10) != "@gmail.com") {
          validresult.error = "Invalid email. Only G-mail currently accepted.";
          //validresult.error = "Invalid email.";
          validresult.validity = false;
        }
      } else if (e.length <= 10) {
        validresult.error = "Invalid length.";
        validresult.validity = false;
      }
    }

    if (!e) {
      validresult.error = "Invalid empty field.";
      validresult.validity = false;
    }
    return validresult;
    /*set valid n stuff*/
  };

  return (
    <>
      {/*<p>modalcontent</p>
      <p>email component - returns value</p>*/}
      <InputField
        valiTime={valiTime}
        cname={4}
        tabState={tabState}
        subject={"Email"}
        input={preEmail}
        value={inputValue.f1}
        setInputValue={setInputValue}
        setInput={setPreEmail}
        setValiTime={setValiTime}
        validator={validator}
      />
      {/*<a>{preEmail}</a>
      <p>pw component - returns value</p>*/}
      <InputField
        valiTime={valiTime}
        cname={3}
        tabState={tabState}
        subject={"Password"}
        input={prePass}
        setInput={setPrePass}
        validator={validator}
        setValiTime={setValiTime}
        value={inputValue.f1}
        onChange={(e) => {
          setInput(e.target.value);
          setValiTime(false);
          setInputValue(e.target.value);
        }}
        setInputValue={setInputValue}
      />
      {console.log(inputValue.f2)}
      {/*<p>{prePass}</p>

      <p>
        submit button - changes function to sign in 1, sign up 2, update pw 3
  </p>*/}
      <SubmitButton
        tabState={tabState}
        setValiTime={setValiTime}
        fieldBlank={fieldBlank}
      />
      {/*<p>sign up/sign in link dpeending on tabState 1 or 2, or none if 3+</p>*/}
      {(() => {
        if (tabState == 1)
          return (
            <>
              <p className={"left"}>
                Don't have an account?
                <span className={"highlight"} onClick={() => setTabState(2)}>
                  {" "}
                  Sign up
                </span>
                <span onClick={() => setTabState(3)} className="right">
                  Forgot Password?
                </span>
              </p>
            </>
          );
        else if (tabState == 2)
          return (
            <p className={"left"}>
              Already have an account?{" "}
              <span className={"highlight"} onClick={() => setTabState(1)}>
                Sign in
              </span>
            </p>
          );
      })()}
    </>
  );
};

export default ModalContent;
