import React, { useState } from "react";
import "./index.css";

const SubmitButton = ({
  tabState,
  setTabState,
  setValiTime,
  listkey,
  setGridList,
  phList,
  gridList,
}) => {
  const buttonDirector = (e) => {
    switch (e) {
      case 1:
        return "Sign In";
      case 2:
        return "Create Account";
      case 3:
        return "Update Password";
      case 5:
        return "Grid Item";
      case 6:
        return "Add to Cart";
      case 7:
        return "Add Product";
      case 8:
        return "Grid";
    }
  };

  const btD2 = (e) => {
    if (e < 4) {
      return "purplebutton";
    } else if (e == 5) {
      return "griditem";
    } else if (e == 6 || e == 7) {
      return "purplebutton";
    } else if (e == 8) {
      return "overall grid idk if this button is needed";
    } else {
      return "nobutton";
    }
  };

  const movebehavior = (e) => {
    switch (e) {
      case 6:
        return 8;
      case 7:
        return 8;
      case 8:
        return 7;
      default:
        return 1;
    }
  };

  return (
    <>
      <button
        onClick={() => {
          setValiTime(true);
          if (tabState == 7) {
            setGridList(...gridList, { product: phList });
          }
          setTabState(movebehavior(tabState));
        }}
        className={btD2(tabState)}
      >
        {buttonDirector(tabState)}
      </button>
      {"   "}
      <button
        className={tabState == 5 ? "griditem2" : "nobutton"}
        onClick={() => setTabState(7)}
      >
        Edit
      </button>
    </>
  );
};

export default SubmitButton;
