import React, { useRef, useState } from "react";
import "./index.css";

import InputField from "./inputfield";
import SubmitButton from "./submitbutton";

const ModalContent = ({
  setTabState,
  tabState,
  inputEmail,
  setInputEmail,
  inputPassword,
  setInputPassword,
}) => {
  const [preEmail, setPreEmail] = useState("");
  const [prePass, setPrePass] = useState("");
  const [valiTime, setValiTime] = useState(false);
  const fieldBlank = useRef(null);

  const validator = (e) => {
    let errors = "";
    //setInterval(() => setValiTime(false), 10000);
    if (!e) {
      errors = "Invalid empty field.";
      return false;
    } else {
      return true;
    }
    /*set valid n stuff*/
  };

  return (
    <>
      {/*<p>modalcontent</p>
      <p>email component - returns value</p>*/}
      <InputField
        valiTime={valiTime}
        cname={4}
        tabState={tabState}
        subject={"Email"}
        input={preEmail}
        setInput={setPreEmail}
        validator={validator}
        ref={fieldBlank}
      />
      {/*<a>{preEmail}</a>
      <p>pw component - returns value</p>*/}
      <InputField
        valiTime={valiTime}
        cname={3}
        tabState={tabState}
        subject={"Password"}
        input={prePass}
        setInput={setPrePass}
        validator={validator}
        setValiTime={setValiTime}
        ref={fieldBlank}
      />
      {/*<p>{prePass}</p>

      <p>
        submit button - changes function to sign in 1, sign up 2, update pw 3
  </p>*/}
      <SubmitButton
        tabState={tabState}
        setValiTime={setValiTime}
        fieldBlank={fieldBlank}
      />
      {/*<p>sign up/sign in link dpeending on tabState 1 or 2, or none if 3+</p>*/}
      {(() => {
        if (tabState == 1)
          return (
            <>
              <p className={"left"}>
                Don't have an account?
                <span className={"highlight"} onClick={() => setTabState(2)}>
                  {" "}
                  Sign up
                </span>
                <span onClick={() => setTabState(3)} className="right">
                  Forgot Password?
                </span>
              </p>
            </>
          );
        else if (tabState == 2)
          return (
            <p className={"left"}>
              Already have an account?{" "}
              <span className={"highlight"} onClick={() => setTabState(1)}>
                Sign in
              </span>
            </p>
          );
      })()}
    </>
  );
};

export default ModalContent;
