import React, { useState, useEffect } from "react";
import "./index.css";
import Login from "../..";

const SubmitButton = ({
  tabState,
  setTabState,
  setValiTime,
  listkey,
  setGridList,
  phList,
  gridList,
  itemnum,
  setPreDest,
  preDest,
  putuser,
  validator,
  cart,
  setCart,
  putposter,
  apiValid,
  checkLogin,
  inputValue,
  setInputValue,
  getLogin,
  putStatus,
  carTotal,
  setCarTotal,
  userName,
  setUserName,
}) => {
  //const [apiValid, setApiValid] = useState(false);
  const [plusMin, setPlusMin] = useState(false);
  const [wtfupdate, setWtfupdate] = useState(0);
  const buttonDirector = (e) => {
    switch (e) {
      case 1:
        return "Sign In";
      case 2:
        return "Create Account";
      case 3:
        return "Update Password";
      case 5:
        return "Add";
      case 6:
        return "Add to Cart";
      case 7:
        return "Save Product";
      case 8:
        return "Grid";
    }
  };

  const btD2 = (e) => {
    if (e < 4) {
      return "purplebutton";
    } else if (e == 5) {
      return "griditem";
    } else if (e == 6) {
      return "purplebuttonleft";
    } else if (e == 7 || e == 8) {
      return "purplebutton";
    } else {
      return "nobutton";
    }
  };

  const movebehavior = (e) => {
    switch (e) {
      case 1:
        return 8;
      case 2:
        return 1;
      case 3:
        return 4;
      case 5:
        return 8;
      case 6:
        return 8;
      case 7:
        return 8;
      case 8:
        return 7;
      default:
        return 1;
    }
  };

  const numfix = (e) => {
    return e;
  };

  const getProducts = async () => {
    const x = await fetch("http://localhost:3001/gridList", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((result) => {
        //console.log("result ==" + JSON.stringify(result[0][1].name));
        return result;
      });
    return x;
  };

  const setholder = async () => {
    try {
      let x = await getProducts();
      setGridList(await getProducts());
    } catch (error) {
      console.log("submitbutton cant find functions??? - " + error);
    }
  };

  const loginholder = async () => {
    try {
      let x = await getLogin();
      let y = 0;
      for (let i in Object.keys(x)) {
        if (Object.keys(x[i]).length > 1) {
          y++;
        }
      }
      putuser(y + 1, inputValue, [], []);
    } catch (error) {
      console.log("signup error - " + error);
    }
  };

  const plusminner = async () => {
    let x = await gridList[itemnum].cartnum;
    if (x > 0) {
      setPlusMin(true);
    }
    /*if (!x) {
      setCarTotal(+carTotal + 0);
    } else {
      setCarTotal((await carTotal) + (await x));
    }*/
    return x;
  };

  const handleUserNamec = (a) => {
    let x = Object.keys(gridList).map((e, i) => {
      let d = a.map((y) => {
        if (i + 1 == y) {
          return gridList[y].cartnum;
        }
      });
      return d;
    });
    let asdf = x.slice(0);
    let c = asdf.filter((e) => {
      return e !== "";
    });
    console.log("poi c" + c);
    console.log("poi x" + x);
    return c;
  };

  const handleUserNamep = () => {
    let x = Object.keys(gridList).filter((e, i) => {
      if (gridList[i].cartnum > 0) {
        return gridList[i].cartnum;
      }
    });
    let y = x.filter((e) => {
      return e !== undefined;
    });
    console.log("poi" + y);
    return y;
  };

  useEffect(() => {
    plusminner();
    /*if (gridList[itemnum].cartnum == 0) {
      let a = Object.assign(gridList);
      setWtfupdate(wtfupdate + 1);
      a[itemnum].cartnum++;
      putposter(itemnum, a[itemnum]);
    }*/
  }, [gridList[itemnum].cartnum, gridList]);

  return (
    <>
      <button
        onClick={() => {
          if (tabState == 1) {
            checkLogin(inputValue.email, inputValue.pw);
          }
          if (tabState == 2) {
            loginholder();
          }
          if (tabState < 4) {
            setValiTime(true);
          } else {
            setValiTime(false);
          }
          if (tabState == 5) {
            if (!plusMin) {
              setPreDest(itemnum);
              setCart(+cart + +gridList[itemnum].price);
              let a = Object.assign(gridList);
              setPlusMin(true);
              a[itemnum].cartnum++;
              setGridList(a);
              putposter(itemnum, a[itemnum]);

              //

              putuser(
                itemnum,
                inputValue,
                handleUserNamep(),
                handleUserNamec(handleUserNamep())
              );
            }
          }
          if (tabState == 6) {
            if (!plusMin) {
              setPreDest(itemnum);
              setCart(+cart + +gridList[itemnum].price);
              let a = Object.assign(gridList);
              setPlusMin(true);
              a[itemnum].cartnum++;
              setGridList(a);
              putposter(itemnum, a[itemnum]);
            }
          }
          if (tabState == 7) {
            console.log("preDest as of here is");
            console.log({ preDest });
            let y = 1;
            for (let i in Object.keys(gridList).slice(1)) {
              if (Object.keys(gridList[i]).length > 1) {
                y++;
              }
            }
            let z = gridList.slice(1, y);
            if (preDest == 0) {
              let x = Object.keys(gridList).length;
              console.log("the index here of y is " + y);
              /*setGridList({
                ...gridList,
                [y]: phList,
              });*/
              console.log("calculated y aka i is" + y);
              putposter(y - 1, phList);
            } else {
              putposter(itemnum, phList);
            }
            setholder();
            y = 0;
          }
          if (tabState == 8) {
            let y = 0;
            for (let i in Object.keys(gridList).slice(1)) {
              y++;
              if (Object.keys(gridList[i]).length == 1) {
                break;
              }
            }
            setPreDest(y);
          }
          setTabState(
            tabState < 4 && !apiValid ? movebehavior(0) : movebehavior(tabState)
          );
        }}
        className={btD2(tabState)}
      >
        {plusMin ? (
          <>
            <span
              className="minus"
              onClick={() => {
                let a = Object.assign(gridList);
                setWtfupdate(wtfupdate - 1);
                if (a[itemnum].cartnum == 1) {
                  setPlusMin(false);
                  setWtfupdate(0);
                }
                a[itemnum].cartnum--;
                setGridList(a);
                putposter(itemnum, a[itemnum]);
              }}
            >
              -
            </span>
            <a>{gridList[itemnum].cartnum}</a>
            <span
              className="plus"
              onClick={() => {
                let a = Object.assign(gridList);
                setWtfupdate(wtfupdate + 1);
                a[itemnum].cartnum++;
                setGridList(a);
                putposter(itemnum, a[itemnum]);
              }}
            >
              +
            </span>
          </>
        ) : (
          <>{buttonDirector(tabState)}</>
        )}
      </button>
      {"   "}
      <button
        className={tabState == 5 || tabState == 6 ? "griditem2" : "nobutton"}
        onClick={() => {
          setTabState(7);
          if (tabState == 5) {
            setPreDest(itemnum);
          }
          {
            /*if (tabState == 7) {
            setGridList({ ...gridList, [itemnum]: phList });
          }*/
          }
        }}
      >
        Edit
      </button>
    </>
  );
};

export default SubmitButton;
