import React, { useState } from "react";
import "./index.css";

interface IProps {
  titleText?: string;
  visible?: boolean;
  children?: JSX.Element;
  tabState: number;
  handleVisible: () => void;
  setTabState: (arg0: number) => void;
  handleTab: (arg0: number) => void;
  width: number;
}

const Modal = (props: IProps) => {
  const {
    children,
    titleText,
    setTabState = () => {},
    visible,
    handleVisible = () => {},
    tabState,
    width,
    handleTab,
  } = props;
  return (
    <>
      {tabState < 4 ? (
        <div className={"modalcontent"}>
          <h2>{titleText}</h2>
          <span
            className={"closer"}
            onClick={() => {
              handleVisible();
              handleTab(1);
            }}
          >
            X
          </span>
          {/*<hr />*/}
          {children}
        </div>
      ) : tabState > 7 ? (
        <div>grid container</div>
      ) : (
        <div className={"modalcontent"}>
          {" "}
          any of the 3 phase 2 modals {children}
        </div>
      )}
    </>
  );
};

export default Modal;
