import React, { useState } from "react";
import "./index.css";

const SubmitButton = ({ tabState, setTabState, setValiTime }) => {
  const [buttonName, setButtonName] = useState("Sign In");

  const buttonDirector = (e) => {
    switch (e) {
      case 1:
        return "Sign In";
      case 2:
        return "Create Account";
      case 3:
        return "Update Password";
      case 6:
        return "Add to Cart";
    }
  };

  return (
    <button
      onClick={() => {
        setValiTime(true);
        setTabState(5);
      }}
      className={tabState < 4 ? "purplebutton" : "nobutton"}
    >
      {buttonDirector(tabState)}
    </button>
  );
};

export default SubmitButton;
