import React, { useState } from "react";
import "./index.css";

import InputField from "./inputfield";
import SubmitButton from "./submitbutton";

const ModalContent = ({
  setTabState,
  tabState,
  inputEmail,
  setInputEmail,
  inputPassword,
  setInputPassword,
}) => {
  const [preEmail, setPreEmail] = useState("");
  const [prePass, setPrePass] = useState("");
  const [isValid, setIsValid] = useState(0);

  const validator = () => {
    let errors = [];
    let x,
      y = 0;

    if (!preEmail) {
      x = 1;
    }

    if (!prePass) {
      y = 2;
    }
    setIsValid(x + y);

    {
      /*set valid n stuff*/
    }
  };

  return (
    <>
      {/*<p>modalcontent</p>
      <p>email component - returns value</p>*/}
      <InputField
        isValid={isValid}
        cname={4}
        tabState={tabState}
        subject={"Email"}
        input={preEmail}
        setInput={setPreEmail}
      />
      <a>{preEmail}</a>
      {/*<p>pw component - returns value</p>*/}
      <InputField
        isValid={isValid}
        cname={3}
        tabState={tabState}
        subject={"Password"}
        input={prePass}
        setInput={setPrePass}
      />
      <p>{prePass}</p>
      {/*
      <p>
        submit button - changes function to sign in 1, sign up 2, update pw 3
  </p>*/}
      <SubmitButton
        tabState={tabState}
        onClick={validator(preEmail, prePass)}
      />
      {/*<p>sign up/sign in link dpeending on tabState 1 or 2, or none if 3+</p>*/}
      {(() => {
        if (tabState == 1)
          return (
            <>
              <p className={"left"}>
                Don't have an account?
                <span className={"highlight"} onClick={() => setTabState(2)}>
                  {" "}
                  Sign up
                </span>
                <span onClick={() => setTabState(3)} className="right">
                  Forgot Password?
                </span>
              </p>
            </>
          );
        else if (tabState == 2)
          return (
            <p className={"left"}>
              Already have an account?{" "}
              <span className={"highlight"} onClick={() => setTabState(1)}>
                Sign in
              </span>
            </p>
          );
      })()}
    </>
  );
};

export default ModalContent;
