import React, { useState } from "react";
import "./index.css";

const InputField = ({ subject, cname, tabState, input, setInput, isValid }) => {
  {
    /*const [isValid, setIsValid] = useState(true); 
old spot*/
  }

  return (
    <div className={tabState < cname ? "field" : "nodisplay"}>
      <label>{subject}</label>
      <br />
      <input
        type="text"
        onChange={(e) => setInput(e.target.value)}
        className={isValid == 1 || isValid == 0 ? "valid" : "invalid"}
      ></input>
    </div>
  );
};

export default InputField;
