import React, { useState, useEffect } from "react";
import "./index.css";
import Login from "../..";

const SubmitButton = ({
  tabState,
  setTabState,
  setValiTime,
  listkey,
  setGridList,
  phList,
  gridList,
  itemnum,
  setPreDest,
  preDest,
  putuser,
  validator,
  cart,
  setCart,
  putposter,
  apiValid,
  checkLogin,
  inputValue,
  setInputValue,
  getLogin,
  putStatus,
}) => {
  //const [apiValid, setApiValid] = useState(false);
  let adder = "";
  const buttonDirector = (e) => {
    switch (e) {
      case 1:
        return "Sign In";
      case 2:
        return "Create Account";
      case 3:
        return "Update Password";
      case 5:
        return "Add";
      case 6:
        return "Add to Cart";
      case 7:
        return "Save Product";
      case 8:
        return "Grid";
    }
  };

  const btD2 = (e) => {
    if (e < 4) {
      return "purplebutton";
    } else if (e == 5) {
      return "griditem";
    } else if (e == 6) {
      return "purplebuttonleft";
    } else if (e == 7 || e == 8) {
      return "purplebutton";
    } else {
      return "nobutton";
    }
  };

  const movebehavior = (e) => {
    switch (e) {
      case 1:
        return 8;
      case 2:
        return 1;
      case 3:
        return 4;
      case 5:
        return 8;
      case 6:
        return 8;
      case 7:
        return 8;
      case 8:
        return 7;
      default:
        return 1;
    }
  };

  const getProducts = async () => {
    const x = await fetch("http://localhost:3001/gridList", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((res) => res.json())
      .then((result) => {
        //console.log("result ==" + JSON.stringify(result[0][1].name));
        return result;
      });
    return x;
  };

  const setholder = async () => {
    try {
      let x = await getProducts();
      setGridList(await getProducts());
    } catch (error) {
      console.log("submitbutton cant find functions??? - " + error);
    }
  };

  const loginholder = async () => {
    try {
      let x = await getLogin();
      let y = 0;
      for (let i in Object.keys(x)) {
        if (Object.keys(x[i]).length > 1) {
          y++;
        }
      }
      putuser(y + 1, inputValue);
    } catch (error) {
      console.log("signup error - " + error);
    }
  };

  useEffect(() => {
    //setholder();
  }, []);

  return (
    <>
      <button
        onClick={() => {
          if (tabState == 1) {
            checkLogin(inputValue.email, inputValue.pw);
          }
          if (tabState == 2) {
            loginholder();
          }
          if (tabState < 4) {
            setValiTime(true);
          } else {
            setValiTime(false);
          }
          if (tabState == 5) {
            setPreDest(itemnum);
            adder = "adder";
            setCart(+cart + +gridList[itemnum].price);
          }
          if (tabState == 6) {
            setCart(+cart + +gridList[itemnum].price);
          }
          if (tabState == 7) {
            console.log("preDest as of here is");
            console.log({ preDest });
            let y = 1;
            for (let i in Object.keys(gridList).slice(1)) {
              if (Object.keys(gridList[i]).length > 1) {
                y++;
              }
            }
            let z = gridList.slice(1, y);
            if (preDest == 0) {
              let x = Object.keys(gridList).length;
              console.log("the index here of y is " + y);
              /*setGridList({
                ...gridList,
                [y]: phList,
              });*/
              console.log("calculated y aka i is" + y);
              putposter(y, phList);
            } else {
              putposter(itemnum, phList);
            }
            setholder();
            y = 0;
          }
          if (tabState == 8) {
            let y = 0;
            for (let i in Object.keys(gridList).slice(1)) {
              y++;
              if (Object.keys(gridList[i]).length == 1) {
                break;
              }
            }
            setPreDest(y);
          }
          setTabState(
            tabState < 4 && !apiValid ? movebehavior(0) : movebehavior(tabState)
          );
        }}
        className={btD2(tabState) + adder}
      >
        {buttonDirector(tabState)}
      </button>
      {"   "}
      <button
        className={tabState == 5 || tabState == 6 ? "griditem2" : "nobutton"}
        onClick={() => {
          setTabState(7);
          if (tabState == 5) {
            setPreDest(itemnum);
          }
          {
            /*if (tabState == 7) {
            setGridList({ ...gridList, [itemnum]: phList });
          }*/
          }
        }}
      >
        Edit
      </button>
    </>
  );
};

export default SubmitButton;
